import React, { Component } from 'react';
import IngredientItem from './IngredientItem';
import Meta from './Meta';
import StyleItem from './StyleItem';

export class RecipeItem extends Component {
    render() {
        const { title, excerpt, meta, ingredient, style } = this.props.recipe;
        return (
            <div className="recipe" >
                <h2>{title.rendered}</h2>
                <h3>En résumé :</h3>
                <div dangerouslySetInnerHTML={{_html: excerpt.rendered}}>{excerpt.rendered}</div>
                <Meta meta={meta} />

                <div className="recipe_ingredient">
                    <h3>Ingredients</h3>
                    <ul className="ingredient_list">
                        {ingredient.map(ingredientItem => (
                            <IngredientItem 
                                key={ingredientItem.id} 
                                ingredient={ingredientItem} 
                            />
                        ))}
                    </ul>
                </div>

                <div className="recipe_style">
                    <h3>Visible dans :</h3>
                    {style.map(styleItem => (
                        <StyleItem 
                            key={styleItem.id} 
                            style={styleItem}
                        />
                    ))}
                </div>
            </div>
        )
    }
}

export default RecipeItem;


