import React, { Component } from 'react';

export class RecipeItem extends Component {
    render() {
        const { title, excerpt, meta, ingredients, style } = this.props.recipe;
        return (
            <div>
                <h2>{title.rendered}</h2>$
                <strong><span>En résumé :</span>{excerpt.rendered}</strong>
                <div>
                    <h5>Créateur:</h5><p>{meta.createur}</p>
                    <h5>Préparation:</h5><p>{meta.preparation}</p>
                    <h5>Cuisson</h5><p>{meta.temps_de_cuisson}</p>
                </div>

            </div>
        )
    }
}

export default RecipeItem;
