import React, { Component } from 'react';

export class Meta extends Component {
    render() {
        const { meta } = this.props;
        // console.log(meta)
        return (
            <div className="recipe_meta">
                <strong>Créateur:</strong><p>{meta.createur}</p>/p><br />
                <strong>Préparation:</strong><p>{meta.preparation}</p><br />
                <strong>Cuisson</strong><p>{meta.temps_de_cuisson}</p><br />
            </div>
        )
    }
}

export default Meta;
