import React, { Component } from 'react';
import IngredientItem from './IngredientItem';

export class RecipeItem extends Component {
    render() {
        const { title, excerpt, meta, ingredient, style } = this.props.recipe;
        return (
            <div className="recipe">
                <h2>{title.rendered}</h2>$
                <h3>En résumé :</h3><p>{excerpt.rendered}</p>
                <div className="recipe_meta">
                    <strong>Créateur:</strong><p>{meta.createur}</p>
                    <strong>Préparation:</strong><p>{meta.preparation}</p>
                    <strong>Cuisson</strong><p>{meta.temps_de_cuisson}</p>
                </div>
                <div className="recipe_ingredient">
                    <h3>Ingredients</h3>
                    <ul className="ingredient_list">
                        <IngredientItem key={ingredient.id} ingredient={ingredient} />
                    </ul>
                </div>
                <div className="recipe_style">
                    <h3>Visible dans :</h3>
                    {style.map(styleItem => (
                        <a key={styleItem.id} href={styleItem.name}>{styleItem.name}</a>
                    ))}
                </div>
            </div>
        )
    }
}

export default RecipeItem;


